mapper.select:
SELECT A.MENU_CODE , A.MENU_NAME , A.MENU_PRICE , A.CATEGORY_CODE , A.ORDERABLE_STATUS FROM TBL_MENU A
WHERE A.CATEGORY_CODE = #{ value }
WHERE A.MENU_NAME LIKE CONCAT('%', #{ value }, '%')

SELECT
       A.MENU_CODE
     , A.MENU_NAME
     , A.MENU_PRICE
     , A.CATEGORY_CODE
     , A.ORDERABLE_STATUS
  FROM TBL_MENU A
 WHERE A.ORDERABLE_STATUS = 'Y'

   AND A.MENU_PRICE  <  #{ price }


   AND A.MENU_PRICE BETWEEN 10000 AND #{ price }


   AND A.MENU_PRICE BETWEEN 20000 AND #{ price }


   AND A.MENU_PRICE BETWEEN 30000 AND #{ price }

SELECT A.MENU_CODE , A.MENU_NAME , A.MENU_PRICE , A.CATEGORY_CODE , A.ORDERABLE_STATUS FROM TBL_MENU A
A.MENU_NAME LIKE CONCAT('%', #{ nameValue }, '%')
AND A.CATEGORY_CODE = #{ categoryValue }
